mapper.select:

SELECT COUNT(nickname)
FROM member
WHERE nickname = #{nickname}


SELECT COUNT(login_id)
FROM member
WHERE login_id = #{loginId}

mapper.update:

UPDATE town
SET total_members = total_members + 1
WHERE town_id = #{townId}


UPDATE member
SET town_id = #{townId}
WHERE member_id = #{memberId}
  AND delete_date IS NULL
